// App.js
import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './App.css';

const slideshowImages = [
  'https://www.hardsoftcomputers.co.uk/wp-content/uploads/2022/09/gallery-iphone-14-pro-new-all-colours-1.gif',
  'https://www.hardsoftcomputers.co.uk/wp-content/uploads/2022/09/gallery-iphone-14-new-all-colours.gif',
  'https://www.tech21.com/cdn/shop/files/eck-nav-dropdown-menu_768x.gif?v=1685712588'
];

function App() {
  useEffect(() => {
    function animateOnScroll(entries, observer) {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('appear');
        }
      });
    }

    const options = {
      threshold: 0.1,
    };
    const observer = new IntersectionObserver(animateOnScroll, options);

    const elements = document.querySelectorAll('.fade-in');
    elements.forEach((element) => {
      observer.observe(element);
    });

    return () => {
      observer.disconnect();
    };
  }, []);

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    pauseOnHover: true,
  };

  return (
    <div>
      <div className="navbar">
        <a href="#">iPhone</a>
        <a href="#">Mac</a>
        <a href="#">Laptop</a>
        <a href="#">AirPods</a>
        <a href="#">Store</a>
        <a href="#">Support</a>
        <a href="#">Watch</a>
      </div>

      <div className="parallax parallax-section1">
        <div className="parallax-content">
          <h1>Welcome to iPhone</h1>
          <p>Discover the new features of the iPhone.</p>
        </div>
      </div>

      <div className="content">
        <h1>iPhone</h1>
        <div className="headline">
          <h1>Designed to be loved</h1>
        </div>
        <Slider {...sliderSettings}>
          {slideshowImages.map((image, index) => (
            <div key={index}>
              <img className="fade-in" src={image} alt={`Slideshow Image ${index + 1}`} />
            </div>
          ))}
        </Slider>
        <h1 className="fade-in">Relax, it's iPhone</h1>
      </div>

      <div className="gif-row">
        <img className="fade-in" src="https://cdn.dribbble.com/users/7171905/screenshots/20301593/media/c1d3a43a31ee5c1d6b0dd7004402a2f1.gif" alt="GIF 1" />
        <img className="fade-in" src="https://www.icegif.com/wp-content/uploads/2022/04/icegif-1266.gif" alt="GIF 2" />
        <img className="fade-in" src="https://www.icegif.com/wp-content/uploads/2023/01/icegif-1907.gif" alt="GIF 3" />
        <img className="fade-in" src="https://cdn.dribbble.com/users/143650/screenshots/2276717/iphone-urbanista-gif.gif" alt="GIF 4" />
      </div>

      <h1 className="fade-in"><center>Shop Now</center></h1>
      <div className="product-grid">
        <div className="product-box fade-in">
          <img src="data:image/jpeg;base64," alt="Product 1" />
          <div className="product-name">iphone pro max</div>
          <div className="product-rate">$999</div>
        </div>
        <div className="product-box fade-in">
          <img src="data:image/jpeg;base64," alt="Product 2" />
          <div className="product-name">iphone 12</div>
          <div className="product-rate">$799</div>
        </div>
        <div className="product-box fade-in">
          <img src="data:image/jpeg;base64," alt="Product 3" />
          <div className="product-name">iphone 13 pro</div>
          <div className="product-rate">$699</div>
        </div>
        <div className="product-box fade-in">
          <img src="data:image/jpeg;base64," alt="Product 4" />
          <div className="product-name">iphone 13</div>
          <div className="product-rate">$899</div>
        </div>
      </div>

      <div className="parallax parallax-section2">
        <div className="parallax-content">
          <center>
          <h1>Innovative Technology</h1>
          <p>Experience the cutting-edge technology of the iPhone.</p>
      </center>
        </div>
      </div>

      <video autoPlay loop muted className="video" controls>
  <source src="https://www.apple.com/105/media/ww/iphone/family/2024/1efec3e0-8619-4684-a57e-6e2310394f08/anim/welcome/large_2x.mp4" type="video/mp4" />
  Your browser does not support the video tag.
</video>


      <div className="parallax parallax-section3">
        <div className="parallax-content">
          <center>
          <h1>Get Yours Today</h1>
          <p>Find the perfect iPhone for you and start enjoying it now.</p>
          </center>
        </div>
      </div>

      <div className="footer">Â© 2024 Amirthavarshini. All rights reserved.</div>
    </div>
  );
}

export default App;
